.sb-plasmic-faq {
  --sb-plasmic-faq-icon-size: 1.5em;
}

.sb-plasmic-faq,
.sb-plasmic-faq * {
  white-space: normal;
}

.sb-plasmic-faq :where(summary) {
  cursor: pointer;
  font-size: 16px;
  position: relative;
  padding-right: calc(var(--sb-plasmic-faq-icon-size) + 1ch);
}

@media screen and (min-width: 600px) {
  .sb-plasmic-faq :where(summary) {
    font-size: 18px;
  }
}

@media screen and (min-width: 800px) {
  .sb-plasmic-faq :where(summary) {
    font-size: 24px;
  }
}

.sb-plasmic-faq :where(summary)::marker {
  content: none;
}

.sb-plasmic-faq :where(summary)::after {
  background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewbox="0 0 24 24"><path d="M7.41 8.59 12 13.17l4.59-4.58L18 10l-6 6-6-6 1.41-1.41z"></path></svg>');
  background-size: contain;
  content: "";
  height: var(--sb-plasmic-faq-icon-size);
  opacity: 0.85;
  position: absolute;
  right: 0;
  top: 0;
  transition: transform 0.3s ease-out;
  width: var(--sb-plasmic-faq-icon-size);
}

.sb-plasmic-faq :where(details[open] summary)::after {
  transform: rotate(180deg);
}

.sb-plasmic-faq :where(summary) :where(h2, h3, h4, h5, h6) {
  display: inline;
  line-height: 1.3;
  font-size: inherit;
  font-weight: 600;
  margin: 0;
}

.sb-plasmic-faq :where(summary + div) {
  display: grid;
  font-size: 14px;
  line-height: 1.5;
  grid-row-gap: 16px;
  margin-top: 16px;
}

@media screen and (min-width: 600px) {
  .sb-plasmic-faq :where(summary + div) {
    font-size: 16px;
  }
}

.sb-plasmic-faq :where(summary + div) > * {
  margin: 0;
}

.sb-plasmic-faq :where(a) {
  color: inherit;
  text-decoration-line: underline;
}
